<style>

.pz-centred {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.vue-pz-content {
    border: #bbb 3px solid;
    background-color: #fff;
    border-radius: 1rem;
    margin: 1rem;
    padding: 1.2rem;
}

td {
    height: 2rem;
    width: 2rem;
    justify-content: center;
    align-items: center;
    display: flex;
    flex-direction: row;
}

table {
    display: flex;
    flex-direction: column;
    margin-bottom: 2rem;
}

tr {
    display: flex;
    flex-direction: row;
}

.finish-info {
    margin-top: 1.75rem;
}

.cansee {
    p,
    td {
        font-family: Georgia, 'Times New Roman', Times, serif;
    }

    .ital {
        font-style: italic;
    }
}

.cannotsee {
    background-color: #fff;
    font-family: 'Courier New', Courier, monospace;

    .countdown-title {
        font-size: 2.5rem;
        color: #000;
        margin-bottom: 2rem;
        font-weight: 300;
        letter-spacing: 2px;
    }

    .countdown-display {
        display: flex;
        justify-content: center;
        gap: 2rem;
        flex-wrap: wrap;
    }

    .time-unit {
        font-size: 1.8rem;
        color: #38c2a2;
        font-weight: bold;
        background: rgba(100, 255, 218, 0.1);
        padding: .75rem 1rem;
        border-radius: 10px;
        border: 2px solid rgba(100, 255, 218, 0.3);
        min-width: 60px;
        box-shadow: 0 4px 15px rgba(100, 255, 218, 0.2);
        transition: all 0.3s ease;
    }

    .time-unit:hover {
        background: rgba(100, 255, 218, 0.2);
        box-shadow: 0 6px 20px rgba(100, 255, 218, 0.3);
    }

    .start-button {
        margin-top: 2rem;
        font-size: 1.6rem;
        padding: 1rem 1.5rem;
        background: linear-gradient(45deg, #58e0c0, #00bcd4);
        color: #fff;
        border: none;
        border-radius: 15px;
        cursor: pointer;
        font-weight: bold;
        letter-spacing: 3px;
        box-shadow: 0 8px 25px rgba(100, 255, 218, 0.4);
        transition: all 0.3s ease;
        text-transform: uppercase;
    }

    .start-button:hover:not(:disabled) {
        transform: translateY(-3px);
        box-shadow: 0 12px 35px rgba(100, 255, 218, 0.6);
        background: linear-gradient(45deg, #4dd0e1, #58dfc0);
    }

    .start-button:active:not(:disabled) {
        transform: translateY(0);
    }

    .start-button:disabled {
        opacity: 0.6;
        cursor: not-allowed;
    }

}

</style>

